// src/components/VerifyOtp.jsx
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import api from '../services/api';

const VerifyOtp = () => {
  const [otp, setOtp] = useState('');
  const location = useLocation();
  const navigate = useNavigate();
  const email = location.state?.email;

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/auth/verify-email', { email, otp });
      alert('✅ Verified! Login now.');
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.message || '❌ OTP failed');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: '50px auto' }}>
      <input value={otp} onChange={(e) => setOtp(e.target.value)} placeholder="Enter OTP" required /> <br /><br />
      <button type="submit">Verify</button>
    </form>
  );
};

export default VerifyOtp;
